#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="D:\新建文件夹\plugins\y-tian-plugin\node_modules\.pnpm\uuid@3.4.0\node_modules\uuid\bin\node_modules;D:\新建文件夹\plugins\y-tian-plugin\node_modules\.pnpm\uuid@3.4.0\node_modules\uuid\node_modules;D:\新建文件夹\plugins\y-tian-plugin\node_modules\.pnpm\uuid@3.4.0\node_modules;D:\新建文件夹\plugins\y-tian-plugin\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/d/新建文件夹/plugins/y-tian-plugin/node_modules/.pnpm/uuid@3.4.0/node_modules/uuid/bin/node_modules:/mnt/d/新建文件夹/plugins/y-tian-plugin/node_modules/.pnpm/uuid@3.4.0/node_modules/uuid/node_modules:/mnt/d/新建文件夹/plugins/y-tian-plugin/node_modules/.pnpm/uuid@3.4.0/node_modules:/mnt/d/新建文件夹/plugins/y-tian-plugin/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../uuid/bin/uuid" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../uuid/bin/uuid" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../uuid/bin/uuid" $args
  } else {
    & "node$exe"  "$basedir/../uuid/bin/uuid" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
